mapper.select:
select wishlist.wishListNum, wishlist.wishListId,
festival.festivalNum, festival.festivalName, festival.festivalAddress,
festival.festivalContent,
festival.festivalStart, festival.festivalFinal, festival.festivalUsetime
from wishlist, festival where festival.festivalNum =
wishlist.wishListFestivalId and wishListId=1 order by wishListNum desc limit #{startRow}, #{pageBlock}

select * from member
where id like #{searchWord}
order by num desc limit #{startRow},#{pageBlock}

select * from member
where name like #{searchWord}
order by num desc limit #{startRow},#{pageBlock}
